import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AccountPage = () => {
  const [user, setUser] = useState(null);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('tesla_token');
    if (!token) {
      navigate('/login');
      return;
    }
    // Fetch user info from backend
    fetch('/api/auth/me', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => {
        if (data && data.email) {
          setUser(data);
        } else {
          setError('Failed to load account info');
        }
      })
      .catch(() => setError('Server error'));
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('tesla_token');
    navigate('/login');
  };

  if (error) {
    return <div style={{ padding: 40, textAlign: 'center', color: '#d32f2f' }}>{error}</div>;
  }

  if (!user) {
    return <div style={{ padding: 40, textAlign: 'center' }}>Loading...</div>;
  }

  return (
    <div style={{ maxWidth: 500, margin: '40px auto', background: '#fff', borderRadius: 12, boxShadow: '0 2px 16px #eee', padding: 32 }}>
      <h2 style={{ fontSize: '2.2rem', fontWeight: 600, marginBottom: 24, textAlign: 'center' }}>My Account</h2>
      <div style={{ fontSize: 18, marginBottom: 16 }}><strong>Email:</strong> {user.email}</div>
      <div style={{ fontSize: 18, marginBottom: 16 }}><strong>Name:</strong> {user.name || 'N/A'}</div>
      <button onClick={handleLogout} style={{ width: '100%', background: '#3957ff', color: '#fff', fontWeight: 600, fontSize: 18, borderRadius: 6, padding: '14px 0', border: 'none', marginTop: 24 }}>Log Out</button>
    </div>
  );
};

export default AccountPage;
